const { User } = require('../../models');
const { verificationSchema } = require('../../schemas');
const { createError, sendEmail } = require('../../helpers');

const { FRONTEND_URL, BASE_URL } = process.env;

const resendVerifyEmail = async (req, res) => {
    const {email} = req.body;
    const {error} = verificationSchema.validate({email});
    if (error) {
        throw createError(400, error.message)
    }

    const user = await User.findOne({email});
    if (!user) {
        throw createError(404);
    }
    if (user.verify) {
        throw createError(400, 'Verification has already been passed')
    }

    const mail = {
        to: email,
        subject: "Welcome to Kapusta",
        html: `<table cellpadding="0" cellspacing="0" width="100%" style="font-family: 'Roboto';">
        <tr>
            <td style="padding: 10px;">
                <a href="${FRONTEND_URL}" target="_blank">
                    <img src="data:image/png;base64," />
                </a>
            </td>
        </tr>
        <tr style="padding: 20px; text-align: center;">
         <td width="100%" height="300px" style="padding-bottom: 50px; 
         border-bottom-left-radius: 70px;  
         background: #F2F5FC;" valign="top">
            <img width="100%" style="margin-top: 10px;" src="data:image/png;base64," />
            <div style="padding: 10px"><h2>Confirm your email address to get started on Kapusta</h2>
            <div style="background-color: #FF751D; border-radius: 16px; 
            width: 150px; 
            padding-top: 15px;
            padding-bottom: 15px; 
            margin: 0 auto;">
                <a target="_blank" href='${BASE_URL}/api/auth/verify/${user.verificationToken}' style="font-family: 'Roboto';
                font-weight: 700;
                font-size: 14px;
                display: block;
                text-align: center;
                text-transform: uppercase;
                text-decoration: none;
                color: #FFFFFF;">
                Confirm email</a>
            </div>
            <p>If you didn’t request this email, there’s nothing to worry about — you can safely ignore it.</p>
            <p>Note: This is an automated response, so please do not reply to this email.</p>
            </div>
         </td>
        </tr>
    </table>`
    }

    await sendEmail(mail);

    res.json({
        message: 'Verification email sent'
    })
}

module.exports = resendVerifyEmail;